  const [customVariable12, setCustomVariable12] = useState('');
  const [variable13, setVariable13] = useState('');
  const [customVariable13, setCustomVariable13] = useState('');
  
  const [variable7, setVariable7] = useState('5.1');
  const [variable8, setVariable8] = useState('1000');
  const [variable9, setVariable9] = useState('3:2');
  const [variable14, setVariable14] = useState('1');
  const [variable15, setVariable15] = useState('');
  const [variable16, setVariable16] = useState('');
  const [customVariable16, setCustomVariable16] = useState('');
  const [variable17, setVariable17] = useState('');
  const [customVariable17, setCustomVariable17] = useState('');

  // Función para generar el prompt
  const generatePrompt = () => {
    const finalVariable1 = variable1 === 'Personalizado' ? customVariable1 : variable1;
    const finalVariable2 = variable2 === 'Personalizado' ? customVariable2 : variable2;
    const finalVariable3 = variable3 === 'Personalizado' ? customVariable3 : variable3;
    const finalVariable4 = variable4 === 'Personalizado' ? customVariable4 : variable4;
    const finalVariable5 = variable5 === 'Personalizado' ? customVariable5 : variable5;
    const finalVariable6 = variable6 === 'Personalizado' ? customVariable6 : variable6;
    const finalVariable10 = variable10 === 'Personalizado' ? customVariable10 : variable10;
    const finalVariable11 = variable11 === 'Personalizado' ? customVariable11 : variable11;
    const finalVariable12 = variable12 === 'Personalizado' ? customVariable12 : variable12;
    const finalVariable13 = variable13 === 'Personalizado' && customVariable13 
                          ? `--no ${customVariable13}` 
                          : '';
    const finalVariable16 = variable16 === 'Personalizado' && customVariable16 
                          ? `--seed ${customVariable16}` 
                          : '';
    const finalVariable17 = variable17 === 'Personalizado' && customVariable17 
                          ? ` ${customVariable17}` 
                          : '';

    const prompt = `Imagina a ${finalVariable1}, la técnica ${finalVariable4}, el estilo ${finalVariable2}, Artista ${finalVariable17}, la atmósfera ${finalVariable3}, el entorno es ${finalVariable5}, plano ${finalVariable6}, clima ${finalVariable10}, la iluminación ${finalVariable11}, momentum ${finalVariable12} --v ${variable7} --s ${variable8} --q ${variable14} --ar ${variable9} ${variable15} ${finalVariable13} ${finalVariable16}`;
    alert(prompt);
  };

  // Función para reiniciar los dropdowns
  const resetFields = () => {
    setVariable1('');
    setCustomVariable1('');
    setVariable2('');
    setCustomVariable2('');
    setVariable3('');
    setCustomVariable3('');
    setVariable4('');
    setCustomVariable4('');
    setVariable5('');
    setCustomVariable5('');
    setVariable6('');
    setCustomVariable6('');
    setVariable10('');
    setCustomVariable10('');
    setVariable11('');
    setCustomVariable11('');
    setVariable12('');
    setCustomVariable12('');
    setVariable13('');
    setCustomVariable13('');    
    setVariable7('5.1');
    setVariable8('1000');
    setVariable9('3:2');
    setVariable14('1');
    setVariable15('');
    setVariable16('');
    setCustomVariable16('');
    setVariable17('');
    setCustomVariable17('');
  };

  // Función para copiar el prompt al portapapeles
  const copyToClipboard = () => {
    const finalVariable1 = variable1 === 'Personalizado' ? customVariable1 : variable1;
    const finalVariable2 = variable2 === 'Personalizado' ? customVariable2 : variable2;
    const finalVariable3 = variable3 === 'Personalizado' ? customVariable3 : variable3;
    const finalVariable4 = variable4 === 'Personalizado' ? customVariable4 : variable4;
    const finalVariable5 = variable5 === 'Personalizado' ? customVariable5 : variable5;
    const finalVariable6 = variable6 === 'Personalizado' ? customVariable6 : variable6;
    const finalVariable10 = variable10 === 'Personalizado' ? customVariable10 : variable10;
    const finalVariable11 = variable11 === 'Personalizado' ? customVariable11 : variable11;
    const finalVariable12 = variable12 === 'Personalizado' ? customVariable12 : variable12;
    
    const finalVariable13 = variable13 === 'Personalizado' && customVariable13 
                          ? `--no ${customVariable13}` 
                          : '';
    const finalVariable16 = variable16 === 'Personalizado' && customVariable16 
                          ? `--seed ${customVariable16}` 
                          : '';
    const finalVariable17 = variable17 === 'Personalizado' && customVariable17 
                          ? `--seed ${customVariable17}` 
                          : '';

    const prompt = `Imagina a ${finalVariable1}, la técnica ${finalVariable4}, el estilo ${finalVariable2}, Artista ${finalVariable17}, la atmósfera ${finalVariable3}, el entorno es ${finalVariable5}, plano ${finalVariable6}, clima ${finalVariable10}, la iluminación ${finalVariable11}, momentum ${finalVariable12} --v ${variable7} --s ${variable8} --q ${variable14} --ar ${variable9} ${variable15} ${finalVariable13} ${finalVariable16}`;
    alert(prompt);

    navigator.clipboard.writeText(prompt).then(function() {
        alert('Prompt copiado al portapapeles');
    }).catch(function(error) {
        alert('Hubo un problema al copiar el prompt: ' + error);
